import React, { Component } from 'react';
import {
    AppRegistry,
    ListView,
    TouchableHighlight,
    ScrollView,
    StyleSheet,
    PanResponder,
    Image,
    Text,
    View,
    RefreshControl,
} from 'react-native';

//swipt
import SwipeitemView from 'react-native-swipe-left';

export default class SwipePage extends Component{
    constructor(props) {
        super(props);
        var ds = new ListView.DataSource({rowHasChanged: (r1, r2) => r1 !== r2});
        this._dataRow = {};
        this.openRowId = '';
        this.state = {
            dataSource: ds.cloneWithRows(['row 1', 'row 2', 'row 3', 'row 4', 'row 5', 'row 6', 'row 7', 'row 8', 'row 9', 'row 10', 'row 11', 'row 12', 'row 13', 'row 14', 'row 15']),
            scrollEnable: true,
            hasIdOpen: false
        };
    }

    _listView() {

        /*
         * android，ios都使用原生下拉刷新组件：
         */
        return (
            <ListView
                style={styles.listview}
                dataSource={this.state.dataSource}
                renderRow={this.rowRender.bind(this)}
                ref="listview"
                renderScrollComponent={(props)=>{
                    return <ScrollView scrollEnabled={this.state.scrollEnable} {...props}/>
                }}
            />
        );
    }


    render() {
        return (
            <View style={{flex:1}}>
                <View style={styles.header}>
                    <Text style={styles.headerText}>消息列表</Text>
                </View>
                {this._listView()}
            </View>
        );
    }

    rowRender(data, a, b) {
        let rightBtn = this._rightButtons();
        let id = '' + a + b;
        return (
            <SwipeitemView
                root={this}
                ref={(row)=>this._dataRow[id] = row}
                id={id}
                data={data}
                rightBtn={rightBtn}>
                <View style={{height: 54}}>
                    <TouchableHighlight
                        onPress={
                            ()=>{
                                alert('hi!');
                            }}>
                        <Text>{data}{a}{b}</Text>
                    </TouchableHighlight>
                </View>
            </SwipeitemView>
        );
    }

    _rightButtons() {
        return [{
            id: 1,
            text: 'button',
            width: 80,
            bgColor: 'red',
            underlayColor: '#ffffff',
            onPress: ()=>{alert('delete1!');},
        }, {
            id: 2,
            image: 'data:image/png;base64,',
            width: 80,
            bgColor: null,
            onPress: ()=>{alert('delete2!')}
        }, {
            id: 3,
            text: 'button',
            width: 80,
            bgColor: 'yellow',
            onPress: ()=>{alert('delete3!');},
        }]
    }
}

const styles = StyleSheet.create({
    listview:{
        flex: 1,
        backgroundColor: '#eeeeee'
    },
    header: {
        height : 50,
        paddingTop:15,
        justifyContent:'center',
        alignItems:'center',
        backgroundColor : '#099fde'
    },
    headerText: {
        color: '#ffffff'
    },
    deletebtn: {
        flex: 1,
        width: 80,
        height: 74,
        backgroundColor: 'red',
        alignItems: 'center',
        justifyContent: 'center'
    },
    btntext: {
        color: '#ffffff'
    },
    deleteBut: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
    },
});
